import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
// import CssBaseline from '@material-ui/core/CssBaseline';
// import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';
import RadioGroup from '@material-ui/core/RadioGroup';
import Radio from '@material-ui/core/Radio';
import Paper from '@material-ui/core/Paper';
import ButtonAppBar from './ButtonAppBar.js';
import { useHistory } from "react-router-dom";
import { loadStripe } from "@stripe/stripe-js";
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormLabel from '@material-ui/core/FormLabel';
import { Elements, StripeProvider } from "@stripe/react-stripe-js";
import CheckoutForm from "./CheckoutForm"

const stripePromise = loadStripe("pk_test_51H9voND7h6J7ftZvWesW0Ak9dpg5Q6sKyjwG6KgHgkmToaqJQC7QcS25pr9PRGekpAwIgKIfliVBw71q4IsCApyw00KxLMKKCD");

const useStyles = makeStyles((theme) => ({
  root: {
    height: '57vh',
    backgroundImage: 'url(https://images.unsplash.com/photo-1571267468911-a8d098c0f900?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1350&q=80)',
    backgroundRepeat: 'no-repeat',
    backgroundColor:
      theme.palette.type === 'light' ? theme.palette.grey[50] : theme.palette.grey[900],
    backgroundSize: 'cover',
    backgroundPosition: 'center',
  },
  root2: {
    height: '38vh',
    backgroundRepeat: 'no-repeat',
    backgroundColor:
      theme.palette.type === 'light' ? theme.palette.grey[50] : theme.palette.grey[900],
    backgroundSize: 'cover',
    backgroundPosition: 'center',
  },
  paper: {
    height: 475,
    width: 300,
  },
  paper2: {
    height: 300,
    width: 500,
  },
  control: {
    padding: theme.spacing(2),
  },
}));

export default function Order(orderProps) {

  const [noodleBin, setNoodleBin] = useState([]);
  const [spacing, setSpacing] = React.useState(0);
  const handleChange = (event) => {
    setSpacing(Number(event.target.value));
  };

  const history = useHistory();

  const classes = useStyles();

  return(
    <div>
    <ButtonAppBar token={orderProps.token}/>
    <Grid container className={classes.root} spacing={4}>
      <Grid item sm>
        <Grid container justify="center" spacing={4}>
          {[0, 1, 2, 3].map((value) => (
            <Grid key={value} item>
              <FormLabel component="legend">Quantity</FormLabel>
              <RadioGroup label="Quantity" name="quantity" aria-label="spacing" value={spacing.toString()} onChange={handleChange} row>
                {[0, 1, 2, 3, 4].map((value) => (
                  <FormControlLabel key={value} value={value.toString()} control={<Radio />} label={value.toString()}/>
                ))}
              </RadioGroup>
              <Paper className={classes.paper} />
            </Grid>
          ))}
        </Grid>
      </Grid>
    </Grid>
    <Grid container className={classes.root2} spacing={4} justify="center">
      <Grid item m>
        <Paper className={classes.paper2}>
        </Paper>    
      </Grid>
      <Grid item m>
        <Paper className={classes.paper2}>
        </Paper>    
      </Grid>
    </Grid>
    </div>
  );
}

